import {Navbar,Button,Nav,NavDropdown,Container} from 'react-bootstrap';
import './Navigation.css'
import { LinkContainer } from 'react-router-bootstrap';
import {useDispatch, useSelector} from 'react-redux'
import { logout, resetNotifications } from '../features/userSlice';
import { useState } from 'react';

function Navigation() {
  const user=useSelector((state)=>state.user);
  const dispatch=useDispatch();
  const bellRef=useRef(null);
  const notificationRef=useRef(null);
  const [bellPos,setBellPos]=useState({});

  const handleLogout=()=>{
    // console.log("he");
    dispatch(logout());
  }
  const unreadNotifications=user?.notifications?.reduce((acc,current)=>{
    if(current.status=='unread') return acc+1;
    return acc;
  },0)

  function handleToggleNotifications(){
    const position=bellRef.current.getBoundingClientRect();
    setBellPos(position);
    notificationRef.current.style.display=notificationRef.current.style.display==="block"?"none":"block";
    dispatch(resetNotifications());
    if(unreadNotifications>1) axios.post('/users/${user._id}/updateNotifications');
  }

  return (
    <Navbar bg="light" expand="lg">
      <Container>
        <LinkContainer to="/">
        <Navbar.Brand>Ecommerce</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {/* if there is no user  */}
            {!user && (<LinkContainer to="/login"><Nav.Link>Login</Nav.Link></LinkContainer>)}

            {user && !user.isAdmin && (
              <LinkContainer to="/cart">
                <Nav.Link>
                  <i className='fas fa-shopping-cart'></i>
                  {user?.cart.count>0 && (
                    <span className='badge badge-warning' id="cartcount">
                      {user.cart.count}
                    </span>
                  )}
                </Nav.Link>
              </LinkContainer>
            )}


            {/* if user  */}
            {user &&
            <NavDropdown title={`${user.email}`} id="basic-nav-dropdown">
              {user.isAdmin && (
                <>
                  <LinkContainer to="/admin">
                   <NavDropdown.Item>Dashboard</NavDropdown.Item>
                   </LinkContainer>
                  <LinkContainer to="/new-product">
                   <NavDropdown.Item>Create Product</NavDropdown.Item>
                   </LinkContainer>
                </>
              )}
             {!user.isAdmin && <>
                  <LinkContainer to="/cart">
                   <NavDropdown.Item>Cart</NavDropdown.Item>
                   </LinkContainer> 
                  <LinkContainer to="/orders">
                   <NavDropdown.Item>My Orders</NavDropdown.Item>
                   </LinkContainer> 
                   </>
              }
              <NavDropdown.Divider />
              <Button className='logout-btn' variant="danger" onClick={()=>handleLogout()}>
                Logout
              </Button>
            </NavDropdown>
            }
          </Nav>
        </Navbar.Collapse>
      </Container>
      {/* notifications  */}
      <div className='notifications-container' style={{position:'absolute'}}>
        {user?.notifications.map((notification)=>(
          <p className={`notification-${notification.status}`}>
            {notification.message}
            <br />
            <span>{notification.time}</span>
          </p>
          
        ))}
      </div>
    </Navbar>
  );
}

export default Navigation;
