import axios from '../axios';
import React, { useState,useEffect } from 'react'
import { useSelector } from 'react-redux';
import Loading from '../components/Loading';
import SimilarProduct from '../components/SimilarProduct';
import { Container,Row,Col,Badge } from 'react-bootstrap';
import AliceCarousel from 'react-alice-carousel';
import { useParams } from 'react-router-dom';
import './ProductPage.css'

function ProductPage() {
    const {id}=useParams();
    const user=useSelector(state=>state.user);
    const [product,setProduct]=useState(null);
    const [similar,setSimilar]=useState(null);

    const handleDragStart=(e)=>{
        e.preventDefault();
    }

    useEffect(()=>{
        axios.get(`/products/${id}`).then(({data})=>{
            setProduct(data.product);
            setSimilar(data.similar);
        })
    },[id])
    if(!product){
        return <Loading />
    }
    const images=product.pictures.map((picture)=> <img className='product__carousel--image' src={picture.url} onDragStart={handleDragStart} />)
    

    let similarProducts=[];
    if(similar){
        similarProducts=similar.map((product,idx)=>{
            <div className='item' data-value={idx}><SimilarProduct {...product} /></div>
        })
    }

  return (
    <Container className="pt-4" style={{position:'relative'}}>
        <Row>
            <Col lg={6}>
                <AliceCarousel mouseTracking items={images} controlsStrategy="alternate" />
            </Col>
            <Col lg={6} className="pt-4">
                <h1>{product.name}</h1>
                <p><Badge bg="primary">{product.category}</Badge></p>
                <p className='product__price'>${product.price} </p>
                <p  style={{textAlign:'justify'}} className='py-3'>
                    <strong>Description:</strong> {product.description}
                </p>
            </Col>
        </Row>
    </Container>
  )
}

export default ProductPage
