import {createApi,fetchBaseQuery} from '@reduxjs/toolkit/query/react';

// create the api

export const appApi=createApi({
    reducerPath:'appApi',
    baseQuery:fetchBaseQuery({baseUrl:'http://localhost:8080'}),
    endpoints:(builder)=>({
        signup:builder.mutation({
            query:user=>({
                url:'/users/signup',
                method:'POST',
                body:user,
            })
        }),
        login:builder.mutation({
            query:user=>({
                url:'/users/login',
                method:'POST',
                body:user
            }),
        }),
        // creating product
        createProduct:builder.mutation({
            query:(product)=>({
                url:'/products',
                body:product,
                method:"POST",
            })
        }),
        // add to Cart
        addToCart:builder.mutation({
            query:(cartInfo)=>({
                url:"/products/add-to-cart",
                body:cartInfo,
                method:"POST",
            })
        }),
        // remove from Cart
        removeFromCart:builder.mutation({
            query:(cartInfo)=>({
                url:"/products/remove-from-cart",
                body:cartInfo,
                method:"POST",
            })
        }),
         // increase Cart product
        increaseCartProduct:builder.mutation({
            query:(body)=>({
                url:"/products/increase-cart",
                body,
                method:"POST",
            })
        }),
        // decrease Cart product
        decreaseCartProduct:builder.mutation({
            query:(body)=>({
                url:"/products/decrease-cart",
                body,
                method:"POST",
            })
        })

    })
})


export const {useSignupMutation,useLoginMutation,useCreateProductMutation,useAddToCartMutation,useDecreaseCartProductMutation,useIncreaseCartProductMutation,useRemoveFromCartMutation}=appApi;

export default appApi;
