import React from 'react'
import './CartPage.css'
import { useSelector } from 'react-redux'
import { Row,Alert,Container,Col,Table } from 'react-bootstrap';

function CartPage() {
    const user=useSelector(state=>state.user);
    const products=useSelector(state=>state.products);
    const userCartObj=user.cart;
    // console.log(products);
    // console.log(userCartObj);
    let cart=products.filter((product)=>userCartObj[product._id]!=null);
    // console.log(user);
  return <Container style={{minHeight:'95vh'}} className="cart-container">
    <Row>
        <Col md={7}>
        <h1 className='pt-2 h3'>Shopping cart</h1>
        {cart.length===0?(
            <Alert variant='info'>Shopping cart is empty. Add products to your cart</Alert>
        ):(<div>

        </div>)}
        </Col>
        <Col md={5}>
          {cart.length>0 && (
            <>
              <Table responsive="sm" className="cart-table">
                <thead>
                  <tr>
                    <th>&nbsp;</th>
                    <th>Product</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map(item=>(
                    <tr>
                      <td>&nbsp;</td>
                      <td><i className='fa fa-times' style={{marginRight:10,cursor:"pointer"}}></i>
                        <img src={item.pictures[0].url} style={{width:100,height:100,objectFit:cover}} />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </>
          )}
        </Col>
    </Row>
  </Container>
}

export default CartPage
