import React, { useEffect, useState } from 'react'
import axios from '../axios';
import Loading from './Loading';

function ClientsAdminPage() {
    const [users,setUsers]=useState(null);
    const [loading,setLoading]=useState(false);
    useEffect(()=>{
        setLoading(true);
        axios.get('/users').then(({data})=>{
            setLoading(false);
            setUsers(data);
        }).catch((e)=>{
            setLoading(false);
            console.log(e);

        })
    })
    if(loading)
    return <Loading />
    if(users.length===0)
    return <h2 className='pt-2 text-center'>No users yet.</h2>
  return (
    <Table responsive striped bordered hover>
        <thead>
            <tr>
                <th>Client Id</th>
                <th>Client name</th>
                <th>Items</th>
                {/* <th>Status</th> */}
                {/* <th>Date</th> */}
                <th>Order Total</th>
                <th>Address</th>
                {/* <th>&nbsp;</th> */}
            </tr>
        </thead>
        <tbody>
            {orders.map(order=>(<tr>
                <td>{order._id}</td>
                <td>{order.owner?.name}</td>
                <td>{order.count}</td>
                <td>${order.total}</td>
                <td>{order.address}</td>
                <td>{order.status==='processing' ?(<Button size="sm" onClick={()=>markShipped(order._id,order.owner?.id)}>Mark as shipped</Button>):<Badge bg="success">Shipped</Badge>}</td>
                <td><span style={{cursor:'pointer'}} onClick={()=>showOrder(order.products)}>
                    View Order <i className="fa fa-eye"></i>
                    </span></td>
            </tr>))}
        </tbody>
    </Table>
  )
}

export default ClientsAdminPage
