import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js'
import React,{useState} from 'react'
import { useSelector } from 'react-redux';
import {useNavigate} from 'react-router-dom';
import { useCreateOrderMutation } from '../services/appApi';
import { Row,Col,Alert,Form } from 'react-bootstrap';

function CheckoutForm({paying,setPaying}) {
    const stripe=useStripe();
    const elements=useElements();
    const user=useSelector(state=>state.user);
    const navigate=useNavigate();
    const [alertMessage,setAlertMessage]=useState("");
    const [createOrder,{isLoading,isError}]=useCreateOrderMutation();
    const [country,setCountry]=useState('');
    const [address,setAddress]=useState("");


    function handlePay(e){

    }

  return <Col md={7} className="cart-payment-container">
    <Form onSubmit={handlePay}>
        <Row>
            {alertMessage && <Alert>{alertMessage}</Alert>}
       
        <Col md={6}>
            <Form.Group className="mb-3">
                <Form.Label>First Name</Form.Label>
                <Form.Control type="text" placeholder="First Name" value={user.name} disabled />
            </Form.Group>
        </Col>
        <Col md={6}>
            <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Email" value={user.email} disabled />
            </Form.Group>
        </Col>
         </Row>
         <Row>
             <Col md={6}>
                <Form.Group className="mb-3">
                    <Form.Label>Address</Form.Label>
                    <Form.Control type="text" placeholder="Address" value={address} onChange={(e)=>setAddress(e.target.value)} />
                </Form.Group>
             </Col>
             <Col md={6}>
                <Form.Group className="mb-3">
                    <Form.Label>Country</Form.Label>
                    <Form.Control type="text" placeholder="Country" value={country} onChange={(e)=>setCountry(e.target.value)} />
                </Form.Group>
             </Col>
         </Row>
         <label htmlFor="card-element">Card</label>
         <CardElement id="card-element" />
         <Button className="mt-3" type="submit" disabled={user.cart.count<=0 || paying}></Button>
    </Form>
  </Col>
}

export default CheckoutForm
