import React, { useEffect, useState } from 'react'
import axios from '../axios';
import Loading from './Loading';

function ClientsAdminPage() {
    const [users,setUsers]=useState(null);
    const [loading,setLoading]=useState(false);
    useEffect(()=>{
        setLoading(true);
        axios.get('/users').then(({data})=>{
            setLoading(false);
            setUsers(data);
        }).catch((e)=>{
            setLoading(false);
            console.log(e);

        })
    })
    if(loading)
    return <Loading />
    if(users.length===0)
    return <h2 className='pt-2 text-center'>No users yet.</h2>
  return (
    <Table responsive striped busered hover>
        <thead>
            <tr>
                <th>Client Id</th>
                <th>Client name</th>
                <th>Email</th>
            </tr>
        </thead>
        <tbody>
            {users.map(user=>(<tr>
                <td>{user._id}</td>
                <td>{user.owner?.name}</td>
                <td>{user.count}</td>
                <td>${user.total}</td>
                <td>{user.address}</td>
                <td>{user.status==='processing' ?(<Button size="sm" onClick={()=>markShipped(user._id,user.owner?.id)}>Mark as shipped</Button>):<Badge bg="success">Shipped</Badge>}</td>
                <td><span style={{cursor:'pointer'}} onClick={()=>showuser(user.products)}>
                    View user <i className="fa fa-eye"></i>
                    </span></td>
            </tr>))}
        </tbody>
    </Table>
  )
}

export default ClientsAdminPage
