import {createSlice} from '@reduxjs/toolkit'

// appApi
import appApi from '../services/appApi'

const initialState=[];

export const productSlice=createSlice({
    name:'product',
    initialState,
    reducers:{
        updateProducts:(_,action)=>{
            return action.payload.data;
        },
    },
})

export default productSlice.reducer;
